FILE=/app/.git

if [ -d "$FILE" ] ; then
    echo "$FILE directory exists already."
else
    rm -rf uniborg
    rm -rf .github
    rm -rf requirements.txt
    git clone https://github.com/prono69/PepeBot pepe
    mv pepe/uniborg .
    mv pepe/.github . 
    mv pepe/.git .
    mv pepe/requirements.txt .
    rm -rf pepe
    python3 ./.github/update.py
fi

FILE=/app/bin/
if [ -d "$FILE" ] ; then
    echo "$FILE directory exists already."
else
    bash ./.github/bins.sh
fi

python3 -m stdborg
